mapper.select:
SELECT id, user_id, username, operation, time, method, params, ip, browser, os, status, remark, type, create_time FROM sys_log
AND username LIKE concat('%',#{username},'%')
AND operation LIKE concat('%',#{operation},'%')
AND status = #{status}
AND type = #{type}
AND DATE_FORMAT(create_time, '%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
ORDER BY create_time
ORDER BY create_time DESC
ORDER BY id DESC
